import React, { Suspense, useMemo } from 'react'
import Input from 'react-bootstrap/InputGroup'

import * as THREE from 'three'
import { Canvas } from "@react-three/fiber";
import { OrbitControls, softShadows } from "@react-three/drei";

import {Container} from "react-bootstrap";
import Box from './Box';
import TexturePlane from './TexturePlane';
import DisplayPlane from './DisplayPlane';

softShadows()

const View = (props) => {

  let points = [
    [0,0,0],
    [1,1,1],
    [-1,1,1]
  ];

  let vertices = useMemo(
    () => 
      points.map(point => new THREE.Vector3(...point), [points] )
    );

  return (

<Container>
  <Canvas shadows style={{ position: "relative", width: 750, height: 750 }}  gl={{ antialias: false }}>
  <ambientLight intensity={0.9} />

  <directionalLight 
  castShadow
  position={[9, 9, 9]} 
  intensity={1.9}  
  shadow-mapSize-width={1024}
  shadow-mapSize-height={1024}
  shadow-camera-far={100}
  shadow-camera-left={-15}
  shadow-camera-right={15}
  shadow-camera-top={20}
  shadow-camera-bottom={-10}

  />

  <OrbitControls enableZoom = {true} />
  <Suspense fallback={null}>

    {/*Generate scene here: */}
    {/*bottom: */}

    <mesh>
    <bufferGeometry>
      <bufferAttribute attachObject={['attributes', 'position']}
      
      />
    </bufferGeometry>
    </mesh>
    {/* top */}


    <DisplayPlane/>

    </Suspense>
  </Canvas>
</Container>
);}

export default View
